import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getContestProblems } from '../services/api';

const SolveContest = () => {
  const { contestId } = useParams();
  const [problems, setProblems] = useState([]);
  const [error, setError] = useState(null);
  const [selectedProblemIndex, setSelectedProblemIndex] = useState(0); // To track the selected tab

  useEffect(() => {
    getContestProblems(contestId)
      .then((response) => {
        if (response.data.contest_problems) {
          setProblems(response.data.contest_problems);
        }
      })
      .catch((err) => setError('Failed to load contest problems.'));
  }, [contestId]);

  const handleTabClick = (index) => {
    setSelectedProblemIndex(index); // Set selected tab on click
  };

  return (
    <div className="solve-contest-container">
      <h2>Contest Problems</h2>
      {error ? (
        <p className="error-message">{error}</p>
      ) : (
        <div className="tabs-container">
          <div className="tabs">
            {problems.length === 0 ? (
              <p>No problems found for this contest.</p>
            ) : (
              problems.map((problem, index) => (
                <button
                  key={problem.id}
                  className={`tab ${selectedProblemIndex === index ? 'active' : ''}`}
                  onClick={() => handleTabClick(index)}
                >
                  Problem-{index + 1}
                </button>
              ))
            )}
          </div>
          <div className="problem-details">
            {problems.length > 0 && (
              <div>
                <h3>{problems[selectedProblemIndex].title}</h3>
                <p>{problems[selectedProblemIndex].description}</p>
                <p><strong>Difficulty:</strong> {problems[selectedProblemIndex].difficulty}</p>
                <p><strong>Example:</strong> {problems[selectedProblemIndex].example}</p>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default SolveContest;
