import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Editor } from '@monaco-editor/react';
import './styles/ProblemDetails.css';

const ProblemDetails = () => {
  const { id } = useParams();
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('python');
  const [submissionResult, setSubmissionResult] = useState([]);
  const [submissions, setSubmissions] = useState([]);  // State for submissions
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const [showCodeModal, setShowCodeModal] = useState(false);  // To toggle the modal for individual code
  const [modalCode, setModalCode] = useState('');  // Code to display in modal
  const [showSubmissionsModal, setShowSubmissionsModal] = useState(false);  // State for the "All Submissions" modal

  // Fetch problem details by ID
  useEffect(() => {
    fetch(`http://localhost:5000/Problems/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Problem not found');
        }
        return response.json();
      })
      .then((data) => setProblem(data))
      .catch((error) => setError(error.message));

    // Fetch submissions by problem ID
    fetch(`http://localhost:5000/submissions/${id}`)
      .then((response) => response.json())
      .then((data) => {
        if (data.submissions && data.submissions.length > 0) {
          setSubmissions(data.submissions);
        } else {
          setSubmissions([]);
        }
      })
      .catch((error) => console.error('Error fetching submissions:', error));
  }, [id]);

  const handleCodeSubmit = () => {
    const userLoggedIn = localStorage.getItem('userLoggedIn') === 'true';
    
    if (!userLoggedIn) {
      setSubmissionResult([{ error: 'Please log in to submit code.' }]);
      return;
    }
  
    const payload = { code, language };
  
    setIsLoading(true);
    fetch(`http://localhost:5000/submitCode/${id}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(payload),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.test_case_results) {
          setSubmissionResult(data.test_case_results);
        } else if (data.error) {
          setSubmissionResult([{ error: data.error }]);
        } else {
          setSubmissionResult([{ error: 'Unexpected response format.' }]);
        }
      })
      .catch(() => setSubmissionResult([{ error: 'Submission failed.' }]))
      .finally(() => setIsLoading(false));
  };

  const openModal = (code) => {
    setModalCode(code);
    setShowCodeModal(true);
  };

  const closeModal = () => {
    setShowCodeModal(false);
  };

  const openSubmissionsModal = () => {
    setShowSubmissionsModal(true);
  };

  const closeSubmissionsModal = () => {
    setShowSubmissionsModal(false);
  };

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!problem) {
    return <div>Loading problem details...</div>;
  }

  return (
    <div className="problem-page">
      <div className="problem-card">
        <div className="problem-title">{problem.title}</div>
        <div className={`problem-difficulty ${problem.difficulty.toLowerCase()}`}>{problem.difficulty}</div>
        <div className="problem-description">{problem.description}</div>
      </div>

      <div className="submission-container">
        <div className="first-row">
          <div className="problem-details">
            <h2>Problem Details</h2>
            <div className="problem-description">{problem.description}</div>
          </div>
          <div className="code-editor-container">
            <div className="editor-header">
              <div className="editor-title">Code Editor</div>
              <select
                className="language-select"
                value={language}
                onChange={(e) => setLanguage(e.target.value)}
              >
                <option value="python">Python</option>
                <option value="java">Java</option>
                <option value="c++">C++</option>
              </select>
            </div>
            <Editor
              height="500px"
              language={language}
              value={code}
              onChange={(newValue) => setCode(newValue)}
            />
            <button
              className="submit-btn"
              onClick={handleCodeSubmit}
              disabled={isLoading}
            >
              {isLoading ? 'Submitting...' : 'Submit Code'}
            </button>
          </div>
        </div>

        <div className="submission-results">
          <h3>Submission Results</h3>
          {submissionResult.map((result, index) => (
            <div key={index} className="submission-row">
              {result.error ? (
                <div className="error">{result.error}</div>
              ) : (
                <>
                  <div>{result.test_case}</div>
                  <div>{result.status}</div>
                  <button
                    className="view-code"
                    onClick={() => openModal(result.code)}
                  >
                    View Code
                  </button>
                </>
              )}
            </div>
          ))}
          {/* Button to open the "All Submissions" modal */}
          <button className="view-all-submissions-btn" onClick={openSubmissionsModal}>
            View All Submissions
          </button>
        </div>
      </div>

      {/* Modal for showing all submissions */}
      {showSubmissionsModal && (
        <div className="code-modal">
          <div className="modal-content">
            <button className="close" onClick={closeSubmissionsModal}>×</button>
            <h3>All Submissions</h3>
            <div className="submissions-container">
              {submissions.map((submission) => (
                <div key={submission.id} className="submission-item">
                  <div>{submission.user}</div>
                  <div>{submission.status}</div>
                  <button
                    className="view-code"
                    onClick={() => openModal(submission.code)}
                  >
                    View Code
                  </button>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Modal for showing individual code */}
      {showCodeModal && (
        <div className="code-modal">
          <div className="modal-content">
            <button className="close" onClick={closeModal}>×</button>
            <pre>{modalCode}</pre>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProblemDetails;
