import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // Import to get URL params
import './styles/ProblemDetails.css';

const ProblemDetails = () => {
  const { id } = useParams(); // Get the problemId from the URL
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('Python');
  const [submissionResult, setSubmissionResult] = useState('');
  const [error, setError] = useState(null);

  // Fetch problem details by ID
  useEffect(() => {
    fetch(`http://localhost:5000/Problems/${id}`)  // Full URL to the backend
      .then((response) => {
        if (!response.ok) {
          throw new Error('Problem not found');
        }
        return response.json();
      })
      .then((data) => setProblem(data))
      .catch((error) => setError(error.message));
  }, [id]); // The hook will re-run when the `id` changes

  const handleCodeSubmit = () => {
    // Example submission logic (API integration)
    const payload = {
      code: code,
      language: language,
    };

    fetch('http://localhost:5000/submitCode', {  // Full URL for submitting code
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(payload),
    })
      .then((response) => response.json())
      .then((data) => setSubmissionResult(data.result))
      .catch(() => setSubmissionResult('Submission failed.'));
  };

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!problem) {
    return <div>Loading problem details...</div>;
  }

  return (
    <div className="problem-details-container">
      {/* Problem Details Section */}
      <div className="problem-details">
        <h2>{problem.title}</h2>
        <p className="difficulty">
          Difficulty: <span className={problem.difficulty.toLowerCase()}>{problem.difficulty}</span>
        </p>
        <p className="description">{problem.description}</p>
      </div>

      {/* Code Editor Section */}
      <div className="code-editor">
        <div className="code-header">
          <span>Selected Language: {language}</span>
          <select 
            className="language-select" 
            value={language} 
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="Python">Python</option>
            <option value="JavaScript">JavaScript</option>
            <option value="Java">Java</option>
            <option value="C++">C++</option>
            <option value="C#">C#</option>
          </select>
        </div>
        <textarea
          className="code-input"
          placeholder="Write your code here..."
          value={code}
          onChange={(e) => setCode(e.target.value)}
        />
        <button className="submit-button" onClick={handleCodeSubmit}>
          Submit
        </button>
        {submissionResult && <p className="submission-result">{submissionResult}</p>}
      </div>
    </div>
  );
};

export default ProblemDetails;
