import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Editor } from '@monaco-editor/react';
import './styles/ProblemDetails.css';

const ProblemDetails = () => {
  const { id } = useParams(); // Get the problem ID from the URL
  const [problem, setProblem] = useState(null);
  const [code, setCode] = useState('');
  const [language, setLanguage] = useState('python');
  const [submissionResult, setSubmissionResult] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Fetch problem details by ID
  useEffect(() => {
    fetch(`http://localhost:5000/Problems/${id}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Problem not found');
        }
        return response.json();
      })
      .then((data) => setProblem(data))
      .catch((error) => setError(error.message));
  }, [id]);

  const handleCodeSubmit = () => {
    const payload = { code, language };

    setIsLoading(true); // Start loading
    fetch(`http://localhost:5000/submitCode/${id}`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(payload),
    })
      .then((response) => response.json())
      .then((data) => {
        if (data.test_case_results) {
          setSubmissionResult(data.test_case_results);
        } else if (data.error) {
          setSubmissionResult([{ error: data.error }]);
        } else {
          setSubmissionResult([{ error: 'Unexpected response format.' }]);
        }
      })
      .catch(() => setSubmissionResult([{ error: 'Submission failed.' }]))
      .finally(() => setIsLoading(false)); // Stop loading
  };

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (!problem) {
    return <div>Loading problem details...</div>;
  }

  return (
    <div className="problem-details-container">
      {/* Problem Details Section */}
      <div className="problem-details">
        <h2>{problem.title}</h2>
        <p className="difficulty">
          Difficulty:{' '}
          <span className={problem.difficulty.toLowerCase()}>{problem.difficulty}</span>
        </p>
        <p className="description">{problem.description}</p>
        
        {/* Example Section */}
        {problem.example_input_output && (
          <div className="example-section">
            <h3>Example</h3>
            <pre className="example">{problem.example_input_output}</pre>
          </div>
        )}
      </div>

      {/* Code Editor Section */}
      <div className="code-editor">
        <div className="code-header">
          <span>Selected Language: {language}</span>
          <select
            className="language-select"
            value={language}
            onChange={(e) => setLanguage(e.target.value)}
          >
            <option value="python">Python</option>
            <option value="javascript">JavaScript</option>
            <option value="java">Java</option>
            <option value="cpp">C++</option>
            <option value="csharp">C#</option>
          </select>
        </div>
        
        {/* Monaco Editor */}
        <div className="editor-container">
          <Editor
            height="400px"
            language={language}
            value={code}
            onChange={(newValue) => setCode(newValue)}
            theme="vs-dark"
          />
        </div>

        <button className="submit-button" onClick={handleCodeSubmit}>
          Submit
        </button>

        {/* Loading Spinner */}
        {isLoading && (
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Processing your submission...</p>
          </div>
        )}

        {/* Submission Results */}
        {submissionResult && !isLoading && (
          <div className="submission-result">
            <h3>Submission Results</h3>
            {submissionResult.map((result, index) => (
              <div key={index} className="test-case-result">
                {result.error ? (
                  <p className="error">{result.error}</p>
                ) : (
                  <>
                    <p><strong>Input:</strong> {result.input}</p>
                    <p><strong>Expected Output:</strong> {result.expected_output}</p>
                    <p><strong>Actual Output:</strong> {result.actual_output}</p>
                    <p><strong>Status:</strong> {result.status}</p>
                    <p><strong>Execution Time:</strong> {result.execution_time}</p>
                    <p><strong>Memory:</strong> {result.memory}</p>
                  </>
                )}
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ProblemDetails;
