import React, { useState } from 'react';
import './styles/AIAssist.css'; // Custom CSS for the AI Assist section

const AIAssist = () => {
  const [userInput, setUserInput] = useState('');
  const [messages, setMessages] = useState([
    { sender: 'AI', text: 'Hello! How can I assist you today?' },
  ]);

  // Handle user input and AI response (mock for now)
  const handleSubmit = (e) => {
    e.preventDefault();
    if (userInput.trim() !== '') {
      setMessages([
        ...messages,
        { sender: 'User', text: userInput },
        { sender: 'AI', text: 'This is a mock AI response.' }, // You can replace this with an actual AI API call
      ]);
      setUserInput('');
    }
  };

  return (
    <div className="ai-assist">
      <h2>AI Assist</h2>
      <div className="chat-box">
        {messages.map((message, index) => (
          <div
            key={index}
            className={`message ${message.sender.toLowerCase()}`}
          >
            <span>{message.sender}:</span>
            <p>{message.text}</p>
          </div>
        ))}
      </div>
      <form onSubmit={handleSubmit} className="chat-input-form">
        <input
          type="text"
          value={userInput}
          onChange={(e) => setUserInput(e.target.value)}
          placeholder="Type your message..."
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
};

export default AIAssist;
